mapper.select:
select c.channelId,c.channelName,r.roleSeq,roleName,DATE_FORMAT(r.createTime,'%Y-%m-%d %H:%i:%s') createTime
from role r,channel c
where c.channelId = r.channelId and r.channelId = #{channelId}

    AND ROLENAME = #{roleName}

        SELECT u.userSeq,u.roleSeq,u.userId,u.userName,
	    u.password,u.sex,u.age,u.idType,u.idNo,u.mobilePhone,u.phone,c.channelName,
	    DATE_FORMAT(u.createTime,'%Y-%m-%d %H:%i:%s') createTime,u.addr
	    from userinfo u ,channel c WHERE u.channelId = c.channelId

            AND u.UserName = #{userName}


            AND u.channelId = #{channelId}

mapper.update:
update role set roleName = #{roleName}

    ,channelId = #{channelId}

WHERE roleSeq = #{roleSeq}

UPDATE USERINFO SET RoleSeq = #{roleSeq}, UserName = #{userName}, Sex = #{sex}, Age = #{age}, IdNo = #{idNo}, MobilePhone = #{mobilePhone}, Phone = #{phone}, customerType = #{customerType},
channelId = #{channel.channelId},
Addr = #{addr} WHERE UserSeq = #{userSeq};

UPDATE channel
        SET channelName = #{channelName},
            updateTime = now(),
            state = #{state}
        WHERE
            channelId = #{channelId}
