mapper.select:
select cl.channelId,cl.channelName,c.createQISeq,c.mchId,c.beginDate,c.actionName,c.endDate,c.number,
c.preservation,DATE_FORMAT(c.createTime,'%Y-%m-%d %H:%i:%s') createTime,DATE_FORMAT(c.updateTime,'%Y-%m-%d %H:%i:%s') updateTime,m.mchName,c.state,c.expireSeconds
from CreateQrcodeImg c,Merchant m,channel cl  WHERE m.mchId = c.mchId and m.channelId = cl.channelId

	AND cl.channelId = #{channelId}


	AND DATE_FORMAT(beginDate,'%Y-%m-%d') >= #{beginDate}


	AND DATE_FORMAT(endDate,'%Y-%m-%d') <= #{endDate}

ORDER BY c.createTime DESC

select str_to_date(c.beginDate,'%Y-%m-%d %H:%i:%s') beginDate,
str_to_date(c.endDate,'%Y-%m-%d %H:%i:%s') endDate,q.sceneStr,q.state qState,c.state cState
from Qrcodeimg q,createqrcodeimg c where c.CreateQISeq = q.CreateQISeq AND sceneStr = #{sceneStr}

	AND q.state = #{state}

ORDER BY q.CREATETIME DESC

mapper.update:
update RedPack SET redPackType = #{redPackType},amountType = #{amountType},
totalAmount = #{totalAmount},wishing = #{wishing},actName = #{actName},remark = #{remark},updateTime = now(),state = #{state}
WHERE redPackSeq = #{redPackSeq}
